//Can generate up to 1.3 Million unique ids
function generateUID() {
   return ("0000" + (Math.random()*Math.pow(36,4) << 0).toString(36)).substr(-4);
}

function fromObjectToArrayString(obj){
    return function(){
        var arr = new Array();
        for(var i = 0, l = obj.length; i<l; ++i){
            arr.push(obj[i]);
        }
        return arr;
    }();
}

function getExpirationDelay(){
    //Currently set to a week
    return 7;
}

var renderInputError = function(clientMessage, consoleMessage) {
    renderError(clientMessage, consoleMessage || "This should have been handled on client side");
};

var renderDatabaseError = function(clientMessage, consoleMessage) {
    renderError(clientMessage, consoleMessage || clientMessage);
};

var renderInternalError = function(clientMessage, consoleMessage) {
    renderError(clientMessage, consoleMessage || "Something went wrong on the server.");
};

var renderError = function(clientMessage, consoleMessage) {
    console.log(consoleMessage);
    res.render('error', 
    {
        title: 'Ribosoft',
        stepTitle: clientMessage
    });    
};

var onSaveHandler = function(successCallback) {
    return function(err, result){
        if (err)
        {
            renderDatabaseError("Something went wrong","Could not save. Render error");
        }
        else{
            successCallback(result);
        }
    };
};

exports.SECONDS_IN_WEEK = 604800;
exports.generateUID = generateUID;
exports.getExpirationDelay = getExpirationDelay;
exports.objectToArrayString = fromObjectToArrayString;
exports.renderInputError = renderInputError;
exports.renderDatabaseError = renderDatabaseError;
exports.renderInternalError = renderInternalError;
exports.onSaveHandler = onSaveHandler;